import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ArrowLeft } from "lucide-react";
import RegistrationsTable, { type Registration } from "./RegistrationsTable";
import QRGenerator from "./QRGenerator";
import QRScanner from "./QRScanner";
import ExportData from "./ExportData";
import { useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useState } from "react";

interface FormDetailsProps {
  formId: number;
  onBack: () => void;
}

export default function FormDetails({ formId, onBack }: FormDetailsProps) {
  const { toast } = useToast();
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 50;

  // Fetch registrations for this specific form with pagination
  const { data: registrationsData, isLoading: loadingRegistrations, refetch: refetchRegistrations } = useQuery<{
    registrations: Registration[];
    total: number;
  }>({
    queryKey: [`/api/admin/forms/${formId}/registrations?limit=${pageSize}&offset=${(currentPage - 1) * pageSize}`],
    queryFn: async () => {
      const response = await fetch(`/api/admin/forms/${formId}/registrations?limit=${pageSize}&offset=${(currentPage - 1) * pageSize}`, {
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to fetch registrations");
      return response.json();
    },
    enabled: !!formId,
  });

  const registrations = registrationsData?.registrations || [];
  const totalCount = registrationsData?.total || 0;

  // Fetch stats for this form
  const { data: stats, refetch: refetchStats } = useQuery<{
    totalRegistrations: number;
    qrCodesGenerated: number;
    totalEntries: number;
    activeRegistrations: number;
  }>({
    queryKey: [`/api/admin/forms/${formId}/stats`],
    queryFn: async () => {
      const response = await fetch(`/api/admin/forms/${formId}/stats`, {
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to fetch stats");
      return response.json();
    },
  });

  const handleGenerateQR = async (id: string) => {
    try {
      const response = await apiRequest("POST", `/api/admin/generate-qr/${id}`, {});
      const data = await response.json();

      toast({
        title: "QR Code Generated",
        description: `QR code created for registration ${id}`,
      });

      refetchRegistrations();
      refetchStats();
    } catch (error: any) {
      toast({
        title: "Generation Failed",
        description: error.message || "Failed to generate QR code",
        variant: "destructive",
      });
    }
  };

  const handleDeleteRegistration = async (id: string) => {
    try {
      const response = await fetch(`/api/admin/registrations/${id}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error("Failed to delete registration");
      }

      toast({
        title: "Success",
        description: "Registration deleted successfully",
      });

      refetchRegistrations();
      refetchStats();
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete registration",
        variant: "destructive",
      });
    }
  };

  const handleRevokeQR = async (id: string) => {
    try {
      const response = await fetch(`/api/admin/revoke-qr/${id}`, {
        method: "POST",
      });

      if (!response.ok) {
        throw new Error("Failed to revoke QR code");
      }

      toast({
        title: "Success",
        description: "QR code revoked successfully",
      });

      refetchRegistrations();
      refetchStats();
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to revoke QR code",
        variant: "destructive",
      });
    }
  };

  const handleVerifyScan = async (ticketId: string) => {
    try {
      const response = await fetch(`/api/verify?t=${ticketId}`, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Verification failed");
      }

      const data = await response.json();

      // Invalidate queries to update the UI
      refetchRegistrations();
      refetchStats();

      return {
        ticketId,
        name: data.registration?.name || "Unknown",
        organization: data.registration?.organization || "N/A",
        groupSize: data.registration?.groupSize || 1,
        scansUsed: data.registration?.scans || 0,
        maxScans: data.registration?.maxScans || 4,
        valid: data.valid,
        message: data.message,
        timestamp: new Date(),
      };
    } catch (error) {
      return {
        ticketId,
        name: "Unknown",
        organization: "N/A",
        groupSize: 1,
        scansUsed: 0,
        maxScans: 4,
        valid: false,
        message: "Invalid ticket or verification failed",
        timestamp: new Date(),
      };
    }
  };

  const handleExport = async (format: string, filter: string) => {
    try {
      const url = `/api/admin/forms/${formId}/export?format=${format}&filter=${filter}`;
      const response = await fetch(url, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Export failed");
      }

      const blob = await response.blob();
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = downloadUrl;

      const extension = format === "pdf" ? "pdf" : format === "json" ? "json" : "csv";
      link.download = `form-${formId}-registrations-${Date.now()}.${extension}`;

      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(downloadUrl);

      toast({
        title: "Export Successful",
        description: `Data exported as ${format.toUpperCase()}`,
      });
    } catch (error: any) {
      toast({
        title: "Export Failed",
        description: error.message || "Failed to export data",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="space-y-6">
      <Button
        variant="outline"
        onClick={onBack}
        data-testid="button-back-to-forms"
      >
        <ArrowLeft className="h-4 w-4 mr-2" />
        Back to Forms
      </Button>

      <Tabs defaultValue="registrations">
        <TabsList className="grid w-full max-w-3xl grid-cols-4">
          <TabsTrigger value="registrations" data-testid="tab-form-registrations">
            Registrations
          </TabsTrigger>
          <TabsTrigger value="generate" data-testid="tab-form-generate">
            Generate QR
          </TabsTrigger>
          <TabsTrigger value="scan" data-testid="tab-form-scan">
            Scan Entry
          </TabsTrigger>
          <TabsTrigger value="export" data-testid="tab-form-export">
            Export
          </TabsTrigger>
        </TabsList>

        <TabsContent value="registrations">
          <RegistrationsTable
            registrations={registrations}
            onGenerateQR={handleGenerateQR}
            onDeleteRegistration={handleDeleteRegistration}
            onRevokeQR={handleRevokeQR}
          />
          {/* Pagination controls would go here, updating currentPage */}
          <div className="flex justify-center mt-4">
            <Button
              onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
              disabled={currentPage === 1 || loadingRegistrations}
              variant="outline"
            >
              Previous
            </Button>
            <span className="mx-4 flex items-center">{currentPage} / {Math.ceil(totalCount / pageSize)}</span>
            <Button
              onClick={() => setCurrentPage(prev => Math.min(prev + 1, Math.ceil(totalCount / pageSize)))}
              disabled={currentPage === Math.ceil(totalCount / pageSize) || loadingRegistrations}
              variant="outline"
            >
              Next
            </Button>
          </div>
        </TabsContent>

        <TabsContent value="generate">
          <QRGenerator
            registrations={registrations}
            onGenerate={handleGenerateQR}
          />
        </TabsContent>

        <TabsContent value="scan">
          <QRScanner
            onScan={handleVerifyScan}
          />
        </TabsContent>

        <TabsContent value="export">
          <ExportData
            stats={stats || {
              totalRegistrations: 0,
              qrCodesGenerated: 0,
              totalEntries: 0,
              activeRegistrations: 0,
            }}
            onExport={handleExport}
          />
        </TabsContent>
      </Tabs>
    </div>
  );
}

server/email.ts.content:
import nodemailer from "nodemailer";
import type { Registration } from "@shared/schema";

const EMAIL_USER = process.env.EMAIL_USER || "";
const EMAIL_PASS = process.env.EMAIL_PASS || "";
const EMAIL_FROM = process.env.EMAIL_FROM || "noreply@event.com";
const SITE_URL = process.env.SITE_URL || "http://localhost:5000";

// Create reusable transporter
const transporter = nodemailer.createTransport({
  service: "gmail",
  host: "smtp.gmail.com",
  port: 587,
  secure: false, // true for 465, false for other ports
  auth: {
    user: EMAIL_USER,
    pass: EMAIL_PASS,
  },
  tls: {
    rejectUnauthorized: false
  }
});

// Log configuration on startup
console.log("ðŸ“§ Email Service Configuration:");
console.log("- Service: Gmail");
console.log("- User:", EMAIL_USER || "NOT CONFIGURED");
console.log("- Password:", EMAIL_PASS ? "âœ… SET (" + EMAIL_PASS.length + " chars)" : "âŒ NOT SET");
console.log("- From:", EMAIL_FROM);

export async function sendQRCodeEmail(
  registration: Registration,
  qrCodeDataUrl: string,
  verificationUrl: string
): Promise<boolean> {
  if (!EMAIL_USER || !EMAIL_PASS) {
    console.error("âŒ Email credentials not configured. Skipping email send.");
    console.error("EMAIL_USER:", EMAIL_USER);
    console.error("EMAIL_PASS:", EMAIL_PASS ? "SET" : "NOT SET");
    return false;
  }

  console.log("ðŸ“§ Attempting to send email to:", registration.email);
  console.log("Using email config:", {
    user: EMAIL_USER,
    from: EMAIL_FROM,
    service: "gmail"
  });

  try {
    // Convert data URL to buffer for attachment
    const base64Data = qrCodeDataUrl.replace(/^data:image\/png;base64,/, "");
    const imageBuffer = Buffer.from(base64Data, 'base64');

    const mailOptions = {
      from: EMAIL_FROM,
      to: registration.email,
      subject: `Your Event QR Code - ${registration.id}`,
      html: `
        <!DOCTYPE html>
        <html>
        <head>
          <style>
            body {
              font-family: Arial, sans-serif;
              line-height: 1.6;
              color: #333;
            }
            .container {
              max-width: 600px;
              margin: 0 auto;
              padding: 20px;
            }
            .header {
              background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
              color: white;
              padding: 30px;
              text-align: center;
              border-radius: 10px 10px 0 0;
            }
            .content {
              background: #f9f9f9;
              padding: 30px;
              border-radius: 0 0 10px 10px;
            }
            .qr-code {
              text-align: center;
              margin: 30px 0;
              padding: 20px;
              background: white;
              border-radius: 10px;
            }
            .qr-code img {
              max-width: 300px;
              height: auto;
            }
            .info-box {
              background: white;
              padding: 20px;
              margin: 20px 0;
              border-left: 4px solid #667eea;
              border-radius: 5px;
            }
            .button {
              display: inline-block;
              padding: 12px 30px;
              background: #667eea;
              color: white;
              text-decoration: none;
              border-radius: 5px;
              margin: 10px 0;
            }
            .footer {
              text-align: center;
              margin-top: 30px;
              padding-top: 20px;
              border-top: 1px solid #ddd;
              color: #666;
              font-size: 12px;
            }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <h1>Your Event Registration</h1>
              <p>Registration ID: ${registration.id}</p>
            </div>

            <div class="content">
              <h2>Hello ${registration.name}!</h2>

              <p>Thank you for registering for our event. Your QR code is ready!</p>

              <div class="info-box">
                <strong>Registration Details:</strong><br>
                Name: ${registration.name}<br>
                Email: ${registration.email}<br>
                Organization: ${registration.organization}<br>
                Group Size: ${registration.groupSize}<br>
                Registration ID: ${registration.id}
              </div>

              <div class="qr-code">
                <h3>Your QR Code</h3>
                <img src="cid:qrcode" alt="QR Code" />
                <p style="color: #666; font-size: 14px;">
                  ðŸ“¥ Download the attached QR code image or show it on your phone at the event
                </p>
              </div>

              <div style="text-align: center;">
                <a href="${verificationUrl}" class="button">View Your Registration</a>
              </div>

              <div class="info-box">
                <strong>Important:</strong><br>
                â€¢ Please save this email or take a screenshot of the QR code<br>
                â€¢ Show this QR code at the event entrance<br>
                â€¢ Your QR code can be scanned once for entry<br>
                â€¢ Keep your registration ID (${registration.id}) handy
              </div>
            </div>

            <div class="footer">
              <p>This is an automated email. Please do not reply to this message.</p>
              <p>If you have any questions, please contact the event organizers.</p>
            </div>
          </div>
        </body>
        </html>
      `,
      attachments: [
        {
          filename: `QR-Code-${registration.id}.png`,
          content: imageBuffer,
          cid: 'qrcode', // Referenced in HTML as <img src="cid:qrcode">
          contentType: 'image/png'
        }
      ]
    };

    const info = await transporter.sendMail(mailOptions);
    console.log(`âœ… QR code email sent successfully to ${registration.email}`);
    console.log("Message ID:", info.messageId);
    console.log("Response:", info.response);
    return true;
  } catch (error: any) {
    console.error("âŒ Error sending QR code email:");
    console.error("Error message:", error.message);
    console.error("Error code:", error.code);
    console.error("Full error:", error);
    return false;
  }
}

// Test email configuration
export async function testEmailConnection(): Promise<boolean> {
  if (!EMAIL_USER || !EMAIL_PASS) {
    console.error("âŒ Email credentials not configured");
    return false;
  }

  try {
    await transporter.verify();
    console.log("âœ… Email server connection verified successfully");
    return true;
  } catch (error: any) {
    console.error("âŒ Email server connection failed:");
    console.error("Error:", error.message);
    return false;
  }
}

// Verify email configuration
export function isEmailConfigured(): boolean {
  return !!(EMAIL_USER && EMAIL_PASS);
}
